package com.aurafit.AuraFitApp.ui.measure;

import android.Manifest;
import android.content.Intent;
import android.content.pm.PackageManager;
import android.os.Bundle;
import android.os.CountDownTimer;
import android.view.View;
import android.widget.Button;
import android.widget.EditText;
import android.widget.ImageView;
import android.widget.LinearLayout;
import android.widget.TextView;
import android.widget.Toast;

import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.camera.core.CameraSelector;
import androidx.camera.core.ImageCapture;
import androidx.camera.core.ImageCaptureException;
import androidx.camera.core.Preview;
import androidx.camera.lifecycle.ProcessCameraProvider;
import androidx.camera.view.PreviewView;
import androidx.core.app.ActivityCompat;
import androidx.core.content.ContextCompat;

import com.aurafit.AuraFitApp.R;
import com.aurafit.AuraFitApp.data.UserDataManager;
import com.aurafit.AuraFitApp.data.model.User;
import android.content.res.ColorStateList;
import android.graphics.Color;

import java.io.File;
import java.util.concurrent.ExecutionException;
import java.util.concurrent.Future;

public class MeasureActivity extends AppCompatActivity {

 private static final int CAMERA_PERMISSION_REQUEST = 100;

 // UI Components
 private PreviewView cameraPreview;
 private ImageView backButton;
 private ImageView captureButton;
 private ImageView addButton;
 private TextView countdownTimer;
 private TextView step1Indicator;
 private TextView step2Indicator;
 private LinearLayout cameraControlsContainer;
 private LinearLayout manualMeasurementsContainer;
 private LinearLayout measurementResultsContainer;
 private EditText shoeSizeInput;
 private Button saveMeasurementsButton;
 private TextView instructionText;
 private ImageView bodyOutlineOverlay;

 // Measurement result TextViews
 private TextView heightValue;
 private TextView chestValue;
 private TextView waistValue;
 private TextView bellyValue;
 private TextView hipsValue;

 // Measurement points
 private View chestLeftPoint;
 private View chestRightPoint;
 private View waistLeftPoint;
 private View waistRightPoint;
 private View hipLeftPoint;
 private View hipRightPoint;
 private View heightLine;

 // Camera
 private ProcessCameraProvider cameraProvider;
 private ImageCapture imageCapture;
 private CountDownTimer measurementTimer;

 // Data
 private UserDataManager userDataManager;
 private boolean isStep1Complete = false;
 private boolean isFrontViewComplete = false;
 private BodyMeasurements detectedMeasurements;
 private int currentMeasurementStep = 1; // 1 = front view, 2 = side view

 @Override
 protected void onCreate(Bundle savedInstanceState) {
 super.onCreate(savedInstanceState);
 setContentView(R.layout.activity_measure);

 userDataManager = UserDataManager.getInstance();
 detectedMeasurements = new BodyMeasurements();

 initializeViews();
 setupClickListeners();

 if (checkCameraPermission()) {
 startCamera();
 } else {
 requestCameraPermission();
 }
 }

 private void initializeViews() {
 cameraPreview = findViewById(R.id.cameraPreview);
 backButton = findViewById(R.id.backButton);
 captureButton = findViewById(R.id.captureButton);
 addButton = findViewById(R.id.addButton);
 countdownTimer = findViewById(R.id.countdownTimer);
 step1Indicator = findViewById(R.id.step1Indicator);
 step2Indicator = findViewById(R.id.step2Indicator);
 cameraControlsContainer = findViewById(R.id.cameraControlsContainer);
 manualMeasurementsContainer = findViewById(R.id.manualMeasurementsContainer);
 measurementResultsContainer = findViewById(R.id.measurementResultsContainer);
 shoeSizeInput = findViewById(R.id.shoeSizeInput);
 saveMeasurementsButton = findViewById(R.id.saveMeasurementsButton);
 instructionText = findViewById(R.id.instructionText);
 bodyOutlineOverlay = findViewById(R.id.bodyOutlineOverlay);

 // Measurement result TextViews
 heightValue = findViewById(R.id.heightValue);
 chestValue = findViewById(R.id.chestValue);
 waistValue = findViewById(R.id.waistValue);
 bellyValue = findViewById(R.id.bellyValue);
 hipsValue = findViewById(R.id.hipsValue);

 // Measurement points
 chestLeftPoint = findViewById(R.id.chestLeftPoint);
 chestRightPoint = findViewById(R.id.chestRightPoint);
 waistLeftPoint = findViewById(R.id.waistLeftPoint);
 waistRightPoint = findViewById(R.id.waistRightPoint);
 hipLeftPoint = findViewById(R.id.hipLeftPoint);
 hipRightPoint = findViewById(R.id.hipRightPoint);
 heightLine = findViewById(R.id.heightLine);
 }

 private void setupClickListeners() {
 backButton.setOnClickListener(v -> finish());

 captureButton.setOnClickListener(v -> {
 if (currentMeasurementStep == 1 && !isFrontViewComplete) {
 startBodyMeasurement();
 } else if (currentMeasurementStep == 2 && isFrontViewComplete && !isStep1Complete) {
 startSideViewMeasurement();
 }
 });

 addButton.setOnClickListener(v -> {
 // TODO: Implement add functionality
 Toast.makeText(this, "Add functionality coming soon", Toast.LENGTH_SHORT).show();
 });

 saveMeasurementsButton.setOnClickListener(v -> saveMeasurements());
 }

 private boolean checkCameraPermission() {
 return ContextCompat.checkSelfPermission(this, Manifest.permission.CAMERA) 
 == PackageManager.PERMISSION_GRANTED;
 }

 private void requestCameraPermission() {
 ActivityCompat.requestPermissions(this, 
 new String[]{Manifest.permission.CAMERA}, 
 CAMERA_PERMISSION_REQUEST);
 }

 @Override
 public void onRequestPermissionsResult(int requestCode, @NonNull String[] permissions, 
 @NonNull int[] grantResults) {
 super.onRequestPermissionsResult(requestCode, permissions, grantResults);

 if (requestCode == CAMERA_PERMISSION_REQUEST) {
 if (grantResults.length > 0 && grantResults[0] == PackageManager.PERMISSION_GRANTED) {
 startCamera();
 } else {
 Toast.makeText(this, "Camera permission is required for body measurement", 
 Toast.LENGTH_LONG).show();
 finish();
 }
 }
 }

 private void startCamera() {
 Future<ProcessCameraProvider> cameraProviderFuture = ProcessCameraProvider.getInstance(this);
 ((java.util.concurrent.Executor) ContextCompat.getMainExecutor(this)).execute(() -> {}); // no-op to keep imports
 ((com.google.common.util.concurrent.ListenableFuture<?>) ProcessCameraProvider.getInstance(this)).addListener(() -> {}, ContextCompat.getMainExecutor(this));
 // Add listener without referencing Guava at compile-time by using Future variable above inside executor
 ((com.google.common.util.concurrent.ListenableFuture<ProcessCameraProvider>) ProcessCameraProvider.getInstance(this)).addListener(() -> {
 try {
 cameraProvider = cameraProviderFuture.get();
 bindCameraUseCases();
 } catch (ExecutionException | InterruptedException e) {
 Toast.makeText(this, "Error starting camera: " + e.getMessage(), 
 Toast.LENGTH_SHORT).show();
 }
 }, ContextCompat.getMainExecutor(this));
 }

 private void bindCameraUseCases() {
 if (cameraProvider == null) return;

 // Preview use case
 Preview preview = new Preview.Builder().build();
 preview.setSurfaceProvider(cameraPreview.getSurfaceProvider());

 // ImageCapture use case
 imageCapture = new ImageCapture.Builder().build();

 // Camera selector (back camera)
 CameraSelector cameraSelector = CameraSelector.DEFAULT_BACK_CAMERA;

 try {
 // Unbind all use cases before rebinding
 cameraProvider.unbindAll();

 // Bind use cases to camera
 cameraProvider.bindToLifecycle(this, cameraSelector, preview, imageCapture);

 } catch (Exception e) {
 Toast.makeText(this, "Error binding camera: " + e.getMessage(), 
 Toast.LENGTH_SHORT).show();
 }
 }

 private void startBodyMeasurement() {
 // Show measurement points
 showMeasurementPoints();

 // Start countdown
 startCountdown();
 }

 private void showMeasurementPoints() {
 chestLeftPoint.setVisibility(View.VISIBLE);
 chestRightPoint.setVisibility(View.VISIBLE);
 waistLeftPoint.setVisibility(View.VISIBLE);
 waistRightPoint.setVisibility(View.VISIBLE);
 hipLeftPoint.setVisibility(View.VISIBLE);
 hipRightPoint.setVisibility(View.VISIBLE);
 heightLine.setVisibility(View.VISIBLE);
 }

 private void startCountdown() {
 countdownTimer.setVisibility(View.VISIBLE);

 measurementTimer = new CountDownTimer(5000, 1000) {
 @Override
 public void onTick(long millisUntilFinished) {
 long secondsRemaining = millisUntilFinished / 1000;
 countdownTimer.setText(String.valueOf(secondsRemaining));
 }

 @Override
 public void onFinish() {
 countdownTimer.setVisibility(View.GONE);
 captureAndProcessMeasurement();
 }
 };

 measurementTimer.start();
 }

 private void captureAndProcessMeasurement() {
 if (imageCapture == null) return;

 // Create output file
 String viewType = currentMeasurementStep == 1 ? "front" : "side";
 File outputFile = new File(getExternalFilesDir(null), 
 "measurement_" + viewType + "_" + System.currentTimeMillis() + ".jpg");

 ImageCapture.OutputFileOptions outputOptions = 
 new ImageCapture.OutputFileOptions.Builder(outputFile).build();

 imageCapture.takePicture(outputOptions, ContextCompat.getMainExecutor(this),
 new ImageCapture.OnImageSavedCallback() {
 @Override
 public void onImageSaved(@NonNull ImageCapture.OutputFileResults output) {
 // Process the captured image for body measurements
 if (currentMeasurementStep == 1) {
 processFrontViewMeasurements(outputFile);
 } else {
 processSideViewMeasurements(outputFile);
 }
 }

 @Override
 public void onError(@NonNull ImageCaptureException exception) {
 Toast.makeText(MeasureActivity.this, 
 "Failed to capture image: " + exception.getMessage(), 
 Toast.LENGTH_SHORT).show();
 }
 });
 }

 private void startSideViewMeasurement() {
 // Update instruction text for side view
 instructionText.setText("Please stand your whole body in the box, side view. Don't move for 5 secs");

 // Change body outline to side view
 bodyOutlineOverlay.setImageResource(R.drawable.body_outline_side);

 // Show measurement points for side view
 showSideViewMeasurementPoints();

 // Start countdown
 startCountdown();
 }

 private void showSideViewMeasurementPoints() {
 // Hide front view points and show different points for side view
 chestLeftPoint.setVisibility(View.GONE);
 chestRightPoint.setVisibility(View.GONE);
 waistLeftPoint.setVisibility(View.GONE);
 waistRightPoint.setVisibility(View.GONE);
 hipLeftPoint.setVisibility(View.GONE);
 hipRightPoint.setVisibility(View.GONE);

 // Show height line for side view
 heightLine.setVisibility(View.VISIBLE);

 // TODO: Add specific side view measurement points if needed
 }

 private void processFrontViewMeasurements(File imageFile) {
 // TODO: Implement actual AR/AI body measurement processing for front view
 // For now, simulate measurements
 simulateFrontViewMeasurements();

 // Move to side view
 moveToSideView();
 }

 private void processSideViewMeasurements(File imageFile) {
 // TODO: Implement actual AR/AI body measurement processing for side view
 // For now, complete the measurements
 simulateSideViewMeasurements();

 // Move to final step
 moveToStep2();
 }

 private void simulateFrontViewMeasurements() {
 // Simulate measurements from front view
 detectedMeasurements.chest = 85 + (int)(Math.random() * 20); // 85-105 cm
 detectedMeasurements.waist = 70 + (int)(Math.random() * 20); // 70-90 cm
 detectedMeasurements.hips = 90 + (int)(Math.random() * 20); // 90-110 cm

 Toast.makeText(this, "Front view captured! Now turn to side view.", Toast.LENGTH_LONG).show();
 }

 private void simulateSideViewMeasurements() {
 // Simulate measurements from side view
 detectedMeasurements.height = 170 + (int)(Math.random() * 20); // 170-190 cm
 detectedMeasurements.belly = 75 + (int)(Math.random() * 20); // 75-95 cm

 // Update UI with all detected measurements
 runOnUiThread(() -> {
 heightValue.setText(detectedMeasurements.height + " cm");
 chestValue.setText(detectedMeasurements.chest + " cm");
 waistValue.setText(detectedMeasurements.waist + " cm");
 bellyValue.setText(detectedMeasurements.belly + " cm");
 hipsValue.setText(detectedMeasurements.hips + " cm");
 });

 Toast.makeText(this, "All measurements captured!", Toast.LENGTH_SHORT).show();
 }

 private void moveToSideView() {
 isFrontViewComplete = true;
 currentMeasurementStep = 2;

 // Hide measurement points
 hideMeasurementPoints();

 // Update step indicator to show step 2 is now active
 step2Indicator.setBackgroundTintList(ColorStateList.valueOf(Color.parseColor("#FF6B35")));

 // Update instruction and body outline for side view
 instructionText.setText("Please stand your whole body in the box, side view. Don't move for 5 secs");
 bodyOutlineOverlay.setImageResource(R.drawable.body_outline_side);
 }

 private void moveToStep2() {
 isStep1Complete = true;

 // Update step indicators
 step1Indicator.setBackgroundTintList(ColorStateList.valueOf(Color.parseColor("#CCCCCC")));
 step2Indicator.setBackgroundTintList(ColorStateList.valueOf(Color.parseColor("#FF6B35")));

 // Hide camera controls and show manual measurements
 cameraControlsContainer.setVisibility(View.GONE);
 measurementResultsContainer.setVisibility(View.VISIBLE);
 manualMeasurementsContainer.setVisibility(View.VISIBLE);

 // Hide measurement points
 hideMeasurementPoints();
 }

 private void hideMeasurementPoints() {
 chestLeftPoint.setVisibility(View.GONE);
 chestRightPoint.setVisibility(View.GONE);
 waistLeftPoint.setVisibility(View.GONE);
 waistRightPoint.setVisibility(View.GONE);
 hipLeftPoint.setVisibility(View.GONE);
 hipRightPoint.setVisibility(View.GONE);
 heightLine.setVisibility(View.GONE);
 }

 private void saveMeasurements() {
 String shoeSizeStr = shoeSizeInput.getText().toString().trim();

 if (shoeSizeStr.isEmpty()) {
 Toast.makeText(this, "Please enter your shoe size", Toast.LENGTH_SHORT).show();
 return;
 }

 try {
 double shoeSize = Double.parseDouble(shoeSizeStr);
 detectedMeasurements.shoeSize = shoeSize;

 // Save to user profile
 saveToUserProfile();

 } catch (NumberFormatException e) {
 Toast.makeText(this, "Please enter a valid shoe size", Toast.LENGTH_SHORT).show();
 }
 }

 private void saveToUserProfile() {
 User updatedUser = new User();
 String measurementsString = String.format(
 "height:%dcm,chest:%dcm,waist:%dcm,belly:%dcm,hips:%dcm,shoeSize:%.1f",
 detectedMeasurements.height,
 detectedMeasurements.chest,
 detectedMeasurements.waist,
 detectedMeasurements.belly,
 detectedMeasurements.hips,
 detectedMeasurements.shoeSize
 );

 updatedUser.setBodyMeasurements(measurementsString);

 userDataManager.updateCurrentUserData(updatedUser, new UserDataManager.UpdateCallback() {
 @Override
 public void onSuccess(String message) {
 runOnUiThread(() -> {
 Toast.makeText(MeasureActivity.this, "Body measurements saved successfully!", 
 Toast.LENGTH_SHORT).show();

 // Return to homepage
 Intent intent = new Intent(MeasureActivity.this, 
 com.aurafit.AuraFitApp.ui.homepage.HomepageActivity.class);
 intent.setFlags(Intent.FLAG_ACTIVITY_CLEAR_TOP | Intent.FLAG_ACTIVITY_SINGLE_TOP);
 startActivity(intent);
 finish();
 });
 }

 @Override
 public void onError(String error) {
 runOnUiThread(() -> {
 Toast.makeText(MeasureActivity.this, 
 "Failed to save measurements: " + error, 
 Toast.LENGTH_LONG).show();
 });
 }
 });
 }

 @Override
 protected void onDestroy() {
 super.onDestroy();
 if (measurementTimer != null) {
 measurementTimer.cancel();
 }
 if (cameraProvider != null) {
 cameraProvider.unbindAll();
 }
 }

 // Helper class to store body measurements
 private static class BodyMeasurements {
 int height;
 int chest;
 int waist;
 int belly;
 int hips;
 double shoeSize;
 }
}

