package com.aurafit.AuraFitApp.ui.register;

import androidx.annotation.Nullable;

/**
 * Data validation state of the register form.
 */
public class RegisterFormState {
 @Nullable
 private Integer usernameError;
 @Nullable
 private Integer emailError;
 @Nullable
 private Integer passwordError;
 @Nullable
 private Integer confirmPasswordError;
 private boolean isDataValid;

 RegisterFormState(@Nullable Integer usernameError, @Nullable Integer emailError, 
 @Nullable Integer passwordError, @Nullable Integer confirmPasswordError) {
 this.usernameError = usernameError;
 this.emailError = emailError;
 this.passwordError = passwordError;
 this.confirmPasswordError = confirmPasswordError;
 this.isDataValid = false;
 }

 RegisterFormState(boolean isDataValid) {
 this.usernameError = null;
 this.emailError = null;
 this.passwordError = null;
 this.confirmPasswordError = null;
 this.isDataValid = isDataValid;
 }

 @Nullable
 Integer getUsernameError() {
 return usernameError;
 }

 @Nullable
 Integer getEmailError() {
 return emailError;
 }

 @Nullable
 Integer getPasswordError() {
 return passwordError;
 }

 @Nullable
 Integer getConfirmPasswordError() {
 return confirmPasswordError;
 }

 boolean isDataValid() {
 return isDataValid;
 }
}

