 recommendationRecyclerView = findViewById(R.id.recommendationRecyclerView);
 hamburgerMenu = findViewById(R.id.hamburgerMenu);
 bookmarkIcon = findViewById(R.id.bookmarkIcon);
 drawerIcon = findViewById(R.id.drawerIcon);
