 cameraIcon = findViewById(R.id.cameraIcon);
 galleryIcon = findViewById(R.id.galleryIcon);

 // Onboarding views
 onboardingContainer = findViewById(R.id.onboardingContainer);
 recommendationContainer = findViewById(R.id.recommendationContainer);
 }


 private void setupRecommendationRecyclerView() {
 List<Recommendation> recommendations = new ArrayList<>();
 recommendations.add(new Recommendation("Denim Jacket Outfit", "Blue jacket with white tee and jeans"));
 recommendations.add(new Recommendation("Cardigan Look", "Red cardigan with wide-leg jeans"));
 recommendations.add(new Recommendation("Button-up Shirt", "Light blue long-sleeved shirt"));
 recommendations.add(new Recommendation("Graphic Tee", "Purple oversized tee with cartoon designs"));

 RecommendationAdapter recommendationAdapter = new RecommendationAdapter(recommendations);
 recommendationRecyclerView.setLayoutManager(new GridLayoutManager(this, 2));
 recommendationRecyclerView.setAdapter(recommendationAdapter);
 }

 private void setupClickListeners() {
 hamburgerMenu.setOnClickListener(v -> Toast.makeText(this, "Menu clicked", Toast.LENGTH_SHORT).show());
 bookmarkIcon.setOnClickListener(v -> Toast.makeText(this, "Bookmark clicked", Toast.LENGTH_SHORT).show());

 drawerIcon.setOnClickListener(v -> Toast.makeText(this, "Gallery clicked", Toast.LENGTH_SHORT).show());
 communityIcon.setOnClickListener(v -> Toast.makeText(this, "Community clicked", Toast.LENGTH_SHORT).show());
 cameraIcon.setOnClickListener(v -> handleCameraClick());
 galleryIcon.setOnClickListener(v -> Toast.makeText(this, "Wardrobe clicked", Toast.LENGTH_SHORT).show());
 }

 /**
 * Check if user has body measurements and show appropriate content
 */
 private void checkOnboardingStatus() {
 userDataManager.getCurrentUserProfile(new UserDataManager.ProfileCallback() {
 @Override
 public void onSuccess(User user) {
 runOnUiThread(() -> {
 // Show onboarding if user doesn't have body measurements yet
 if (user.getBodyMeasurements() == null || user.getBodyMeasurements().isEmpty()) {
 showOnboarding();
 } else {
 showRecommendations();
 }
 });
 }

 @Override
 public void onError(String error) {
 runOnUiThread(() -> {
 // If we can't get user data, assume new user and show onboarding
 showOnboarding();
 });
 }
 });
 }

 /**
 * Show onboarding content and hide recommendations
 */
 private void showOnboarding() {
 onboardingContainer.setVisibility(View.VISIBLE);
 recommendationContainer.setVisibility(View.GONE);
 }

 /**
 * Show recommendations and hide onboarding
 */
 private void showRecommendations() {
 onboardingContainer.setVisibility(View.GONE);
 recommendationContainer.setVisibility(View.VISIBLE);
 }

 /**
 * Handle camera click for body measurements
 */
 private void handleCameraClick() {
 // Open AR camera for body measurements
 Intent intent = new Intent(this, com.aurafit.AuraFitApp.ui.measure.MeasureActivity.class);
 startActivity(intent);
 }

 /**
 * Simulate measurements being saved (for testing)
 */
 private void simulateMeasurementsSaved() {
 User updatedUser = new User();
 updatedUser.setBodyMeasurements("height:170cm,chest:90cm,waist:75cm,hips:95cm");

 userDataManager.updateCurrentUserData(updatedUser, new UserDataManager.UpdateCallback() {
 @Override
 public void onSuccess(String message) {
 runOnUiThread(() -> {
 Toast.makeText(HomepageActivity.this, "Body measurements saved!", Toast.LENGTH_SHORT).show();
 showRecommendations();
 });
 }

 @Override
 public void onError(String error) {
 runOnUiThread(() -> {
 Toast.makeText(HomepageActivity.this, "Failed to save measurements: " + error, Toast.LENGTH_SHORT).show();
 });
 }
 });
 }

}
