 super.onCreate(savedInstanceState);
 setContentView(R.layout.activity_measure);

 userDataManager = UserDataManager.getInstance();
 detectedMeasurements = new BodyMeasurements();

 initializeViews();
 setupClickListeners();

 // Request all necessary permissions upfront
 requestAllPermissions();
 }

 private void initializeViews() {
 cameraPreview = findViewById(R.id.cameraPreview);
 backButton = findViewById(R.id.backButton);
 captureButton = findViewById(R.id.captureButton);
 addButton = findViewById(R.id.addButton);
 countdownTimer = findViewById(R.id.countdownTimer);
 cameraControlsContainer = findViewById(R.id.cameraControlsContainer);
 instructionText = findViewById(R.id.instructionText);
 bodyOutlineOverlay = findViewById(R.id.bodyOutlineOverlay);

 // Measurement points
 chestLeftPoint = findViewById(R.id.chestLeftPoint);
 chestRightPoint = findViewById(R.id.chestRightPoint);
 waistLeftPoint = findViewById(R.id.waistLeftPoint);
 waistRightPoint = findViewById(R.id.waistRightPoint);
 hipLeftPoint = findViewById(R.id.hipLeftPoint);
 hipRightPoint = findViewById(R.id.hipRightPoint);
 heightLine = findViewById(R.id.heightLine);
 }

 private void setupClickListeners() {
 backButton.setOnClickListener(v -> finish());

 captureButton.setOnClickListener(v -> {
 if (!isMeasurementComplete) {
 startBodyMeasurement();
 }
 });

 addButton.setOnClickListener(v -> {
 if (checkStoragePermission()) {
 openGallery();
 } else {
 Toast.makeText(this, getString(R.string.storage_permission_required),
 Toast.LENGTH_LONG).show();
 }
 });

 }

 private boolean checkCameraPermission() {
 return ContextCompat.checkSelfPermission(this, Manifest.permission.CAMERA) 
 == PackageManager.PERMISSION_GRANTED;
 }

 private void requestCameraPermission() {
 ActivityCompat.requestPermissions(this, 
 new String[]{Manifest.permission.CAMERA}, 
 CAMERA_PERMISSION_REQUEST);
 }

 private boolean checkStoragePermission() {
 if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.TIRAMISU) {
 // Android 13+ (API 33+)
 return ContextCompat.checkSelfPermission(this, Manifest.permission.READ_MEDIA_IMAGES)
 == PackageManager.PERMISSION_GRANTED;
 } else {
 // Android 12 and below
 return ContextCompat.checkSelfPermission(this, Manifest.permission.READ_EXTERNAL_STORAGE)
 == PackageManager.PERMISSION_GRANTED;
 }
 }

 private boolean checkAllPermissions() {
 return checkCameraPermission() && checkStoragePermission();
 }

 private void requestAllPermissions() {
 if (checkAllPermissions()) {
 // All permissions granted, start camera
 startCamera();
 } else {
 // Request missing permissions
 List<String> permissions = new ArrayList<>();

 if (!checkCameraPermission()) {
 permissions.add(Manifest.permission.CAMERA);
 }

 if (!checkStoragePermission()) {
 if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.TIRAMISU) {
 permissions.add(Manifest.permission.READ_MEDIA_IMAGES);
 } else {
 permissions.add(Manifest.permission.READ_EXTERNAL_STORAGE);
 }
 }

 ActivityCompat.requestPermissions(this,
 permissions.toArray(new String[0]),
 ALL_PERMISSIONS_REQUEST);
 }
 }

 private void requestStoragePermission() {
 if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.TIRAMISU) {
 ActivityCompat.requestPermissions(this,
 new String[]{Manifest.permission.READ_MEDIA_IMAGES},
 STORAGE_PERMISSION_REQUEST);
 } else {
 ActivityCompat.requestPermissions(this,
 new String[]{Manifest.permission.READ_EXTERNAL_STORAGE},
 STORAGE_PERMISSION_REQUEST);
 }
 }

 private void openGallery() {
 try {
 Intent intent = new Intent(Intent.ACTION_PICK, MediaStore.Images.Media.EXTERNAL_CONTENT_URI);
 intent.setType("image/*");

 // Add extra options for better compatibility
 intent.putExtra("crop", "true");
 intent.putExtra("aspectX", 1);
 intent.putExtra("aspectY", 2); // Portrait aspect ratio for body photos

 if (intent.resolveActivity(getPackageManager()) != null) {
 startActivityForResult(intent, GALLERY_REQUEST);
 } else {
 // Fallback to document picker if gallery is not available
 Intent fallbackIntent = new Intent(Intent.ACTION_GET_CONTENT);
 fallbackIntent.setType("image/*");
 fallbackIntent.addCategory(Intent.CATEGORY_OPENABLE);

 if (fallbackIntent.resolveActivity(getPackageManager()) != null) {
 startActivityForResult(Intent.createChooser(fallbackIntent, getString(R.string.select_image)), GALLERY_REQUEST);
 } else {
 Toast.makeText(this, getString(R.string.no_gallery_app), Toast.LENGTH_SHORT).show();
 }
 }
 } catch (Exception e) {
 Toast.makeText(this, "Error opening gallery: " + e.getMessage(), Toast.LENGTH_SHORT).show();
 }
 }

 @Override
 public void onRequestPermissionsResult(int requestCode, @NonNull String[] permissions, 
 @NonNull int[] grantResults) {
 super.onRequestPermissionsResult(requestCode, permissions, grantResults);

 if (requestCode == ALL_PERMISSIONS_REQUEST) {
 boolean cameraGranted = false;
 boolean storageGranted = false;

 for (int i = 0; i < permissions.length; i++) {
 if (permissions[i].equals(Manifest.permission.CAMERA)) {
 cameraGranted = grantResults[i] == PackageManager.PERMISSION_GRANTED;
 } else if (permissions[i].equals(Manifest.permission.READ_EXTERNAL_STORAGE) ||
 permissions[i].equals(Manifest.permission.READ_MEDIA_IMAGES)) {
 storageGranted = grantResults[i] == PackageManager.PERMISSION_GRANTED;
 }
 }

 if (cameraGranted) {
 startCamera();
 if (!storageGranted) {
 Toast.makeText(this, getString(R.string.storage_permission_optional),
 Toast.LENGTH_LONG).show();
 }
 } else {
 Toast.makeText(this, getString(R.string.camera_permission_required),
 Toast.LENGTH_LONG).show();
 finish();
 }
 } else if (requestCode == CAMERA_PERMISSION_REQUEST) {
 if (grantResults.length > 0 && grantResults[0] == PackageManager.PERMISSION_GRANTED) {
 startCamera();
 } else {
 Toast.makeText(this, getString(R.string.camera_permission_required),
 Toast.LENGTH_LONG).show();
 finish();
 }
 } else if (requestCode == STORAGE_PERMISSION_REQUEST) {
 if (grantResults.length > 0 && grantResults[0] == PackageManager.PERMISSION_GRANTED) {
 openGallery();
 } else {
 Toast.makeText(this, getString(R.string.storage_permission_required),
 Toast.LENGTH_LONG).show();
 }
 }
 }

 @Override
 protected void onActivityResult(int requestCode, int resultCode, Intent data) {
 super.onActivityResult(requestCode, resultCode, data);

 if (requestCode == GALLERY_REQUEST && resultCode == RESULT_OK && data != null) {
 Uri selectedImageUri = data.getData();
 if (selectedImageUri != null) {
 processUploadedImage(selectedImageUri);
 } else {
 Toast.makeText(this, getString(R.string.failed_to_select_image), Toast.LENGTH_SHORT).show();
 }
 }
 }

 private void processUploadedImage(Uri imageUri) {
 try {
 // Show loading message
 Toast.makeText(this, getString(R.string.processing_image), Toast.LENGTH_SHORT).show();

 // Convert URI to file path for processing
 String imagePath = getRealPathFromURI(imageUri);
 if (imagePath != null) {
 File imageFile = new File(imagePath);
 if (imageFile.exists()) {
 processBodyMeasurements(imageFile);
 Toast.makeText(this, getString(R.string.photo_uploaded_success), Toast.LENGTH_SHORT).show();
 } else {
 // If file doesn't exist at path, work directly with URI
 processBodyMeasurementsFromUri(imageUri);
 Toast.makeText(this, getString(R.string.photo_uploaded_success), Toast.LENGTH_SHORT).show();
 }
 } else {
 // Work directly with URI if path conversion fails
 processBodyMeasurementsFromUri(imageUri);
 Toast.makeText(this, getString(R.string.photo_uploaded_success), Toast.LENGTH_SHORT).show();
 }
 } catch (Exception e) {
 Toast.makeText(this, "Error processing uploaded image: " + e.getMessage(),
 Toast.LENGTH_LONG).show();
 }
 }

 private void processBodyMeasurementsFromUri(Uri imageUri) {
 // TODO: Implement actual AR/AI body measurement processing from URI
 // For now, simulate measurements
 simulateBodyMeasurements();
 // Navigation is handled in simulateBodyMeasurements() -> navigateToEditMeasurements()
 }

 private String getRealPathFromURI(Uri contentUri) {
 try {
 String[] proj = {MediaStore.Images.Media.DATA};
 Cursor cursor = this.getContentResolver().query(contentUri, proj, null, null, null);
 if (cursor != null) {
 int column_index = cursor.getColumnIndexOrThrow(MediaStore.Images.Media.DATA);
 cursor.moveToFirst();
 String path = cursor.getString(column_index);
 cursor.close();
 return path;
 }
 } catch (Exception e) {
 // Return null if unable to get real path - we'll work with URI directly
 }
 return null;
 }

 private void startCamera() {
 Future<ProcessCameraProvider> cameraProviderFuture = ProcessCameraProvider.getInstance(this);
 ((java.util.concurrent.Executor) ContextCompat.getMainExecutor(this)).execute(() -> {}); // no-op to keep imports
 ((com.google.common.util.concurrent.ListenableFuture<?>) ProcessCameraProvider.getInstance(this)).addListener(() -> {}, ContextCompat.getMainExecutor(this));
 // Add listener without referencing Guava at compile-time by using Future variable above inside executor
 ((com.google.common.util.concurrent.ListenableFuture<ProcessCameraProvider>) ProcessCameraProvider.getInstance(this)).addListener(() -> {
 try {
 cameraProvider = cameraProviderFuture.get();
 bindCameraUseCases();
 } catch (ExecutionException | InterruptedException e) {
 Toast.makeText(this, "Error starting camera: " + e.getMessage(), 
 Toast.LENGTH_SHORT).show();
 }
 }, ContextCompat.getMainExecutor(this));
 }

 private void bindCameraUseCases() {
 if (cameraProvider == null) return;

 // Preview use case
 Preview preview = new Preview.Builder().build();
 preview.setSurfaceProvider(cameraPreview.getSurfaceProvider());

 // ImageCapture use case
 imageCapture = new ImageCapture.Builder().build();

 // Camera selector (front camera)
 CameraSelector cameraSelector = CameraSelector.DEFAULT_FRONT_CAMERA;

 try {
 // Unbind all use cases before rebinding
 cameraProvider.unbindAll();

 // Bind use cases to camera
 cameraProvider.bindToLifecycle(this, cameraSelector, preview, imageCapture);

 } catch (Exception e) {
 Toast.makeText(this, "Error binding camera: " + e.getMessage(), 
 Toast.LENGTH_SHORT).show();
 }
 }

 private void startBodyMeasurement() {
 // Show measurement points
 showMeasurementPoints();

 // Start countdown
 startCountdown();
 }

 private void showMeasurementPoints() {
 chestLeftPoint.setVisibility(View.VISIBLE);
 chestRightPoint.setVisibility(View.VISIBLE);
 waistLeftPoint.setVisibility(View.VISIBLE);
 waistRightPoint.setVisibility(View.VISIBLE);
 hipLeftPoint.setVisibility(View.VISIBLE);
 hipRightPoint.setVisibility(View.VISIBLE);
 heightLine.setVisibility(View.VISIBLE);
 }

 private void startCountdown() {
 countdownTimer.setVisibility(View.VISIBLE);

 measurementTimer = new CountDownTimer(5000, 1000) {
 @Override
 public void onTick(long millisUntilFinished) {
 long secondsRemaining = millisUntilFinished / 1000;
 countdownTimer.setText(String.valueOf(secondsRemaining));
 }

 @Override
 public void onFinish() {
 countdownTimer.setVisibility(View.GONE);
 captureAndProcessMeasurement();
 }
 };

 measurementTimer.start();
 }

 private void captureAndProcessMeasurement() {
 if (imageCapture == null) return;

 // Create output file
 File outputFile = new File(getExternalFilesDir(null), 
 "measurement_" + System.currentTimeMillis() + ".jpg");

 ImageCapture.OutputFileOptions outputOptions = 
 new ImageCapture.OutputFileOptions.Builder(outputFile).build();

 imageCapture.takePicture(outputOptions, ContextCompat.getMainExecutor(this),
 new ImageCapture.OnImageSavedCallback() {
 @Override
 public void onImageSaved(@NonNull ImageCapture.OutputFileResults output) {
 // Process the captured image for body measurements
 processBodyMeasurements(outputFile);
 }

 @Override
 public void onError(@NonNull ImageCaptureException exception) {
 Toast.makeText(MeasureActivity.this, 
 "Failed to capture image: " + exception.getMessage(), 
 Toast.LENGTH_SHORT).show();
 }
 });
 }


