 private void simulateBodyMeasurements() {
 // Simulate all measurements from single photo
 detectedMeasurements.height = 170 + (int)(Math.random() * 20); // 170-190 cm
 detectedMeasurements.chest = 85 + (int)(Math.random() * 20); // 85-105 cm
 detectedMeasurements.waist = 70 + (int)(Math.random() * 20); // 70-90 cm
 detectedMeasurements.belly = 75 + (int)(Math.random() * 20); // 75-95 cm
 detectedMeasurements.hips = 90 + (int)(Math.random() * 20); // 90-110 cm
 detectedMeasurements.midThigh = 50 + (int) (Math.random() * 10); // 50-60 cm

 Toast.makeText(this, getString(R.string.body_measurements_captured), Toast.LENGTH_SHORT).show();

 // Navigate to EditMeasurementsActivity instead of showing inline editing
 navigateToEditMeasurements();
 }

 private void navigateToEditMeasurements() {
 Intent intent = new Intent(this, EditMeasurementsActivity.class);

 // Pass detected measurements to the edit activity
 intent.putExtra(EditMeasurementsActivity.EXTRA_HEIGHT, detectedMeasurements.height);
 intent.putExtra(EditMeasurementsActivity.EXTRA_CHEST, detectedMeasurements.chest);
 intent.putExtra(EditMeasurementsActivity.EXTRA_WAIST, detectedMeasurements.waist);
 intent.putExtra(EditMeasurementsActivity.EXTRA_BELLY, detectedMeasurements.belly);
 intent.putExtra(EditMeasurementsActivity.EXTRA_HIPS, detectedMeasurements.hips);
 intent.putExtra(EditMeasurementsActivity.EXTRA_MID_THIGH, detectedMeasurements.midThigh);
 intent.putExtra(EditMeasurementsActivity.EXTRA_SHOE_SIZE, 9.5f); // Default shoe size

 startActivity(intent);
 finish(); // Close the measure activity
 }



